package org.springframework.samples.petclinic.vet;

import org.junit.jupiter.api.BeforeEach;
import org.junit.jupiter.api.Test;
import org.junit.jupiter.api.DisplayName;
import org.junit.jupiter.api.extension.ExtendWith;
import org.mockito.junit.jupiter.MockitoExtension;

import java.util.Set;
import java.util.List;

import static org.junit.jupiter.api.Assertions.*;

@ExtendWith(MockitoExtension.class)
class VetTest {

	private Vet instance;

	@BeforeEach
	void setUp() {
		instance = new Vet();
		instance.setFirstName("John");
		instance.setLastName("Doe");
	}

	// =========================================================================
	// Tests for addSpecialty(Specialty specialty)
	// =========================================================================

	@Test
	@DisplayName("addSpecialty should add specialty to internal set")
	void addSpecialty_withNewSpecialty_addsToInternalSet() {
		Specialty specialty = new Specialty();

		instance.addSpecialty(specialty);

		assertTrue(instance.getSpecialtiesInternal().contains(specialty));
		assertEquals(1, instance.getNrOfSpecialties());
	}

	@Test
	@DisplayName("addSpecialty should not add duplicate specialty")
	void addSpecialty_withDuplicateSpecialty_doesNotDuplicate() {
		Specialty specialty = new Specialty();
		instance.addSpecialty(specialty);

		instance.addSpecialty(specialty);

		assertEquals(1, instance.getSpecialtiesInternal().size());
		assertEquals(1, instance.getNrOfSpecialties());
	}

	// =========================================================================
	// Tests for getSpecialtiesInternal()
	// =========================================================================

	@Test
	@DisplayName("getSpecialtiesInternal should return empty set initially")
	void getSpecialtiesInternal_initially_returnsEmptySet() {
		Set<Specialty> result = instance.getSpecialtiesInternal();

		assertNotNull(result);
		assertTrue(result.isEmpty());
	}

	@Test
	@DisplayName("getSpecialtiesInternal should return set with added specialties")
	void getSpecialtiesInternal_withAddedSpecialties_returnsSetWithThem() {
		Specialty s1 = new Specialty();
		Specialty s2 = new Specialty();
		instance.addSpecialty(s1);
		instance.addSpecialty(s2);

		Set<Specialty> result = instance.getSpecialtiesInternal();

		assertEquals(2, result.size());
		assertTrue(result.contains(s1));
		assertTrue(result.contains(s2));
	}

	// =========================================================================
	// Tests for getSpecialties()
	// =========================================================================

	@Test
	@DisplayName("getSpecialties should return empty list initially")
	void getSpecialties_initially_returnsEmptyList() {
		List<Specialty> result = instance.getSpecialties();

		assertNotNull(result);
		assertTrue(result.isEmpty());
	}

	@Test
	@DisplayName("getSpecialties should return list with added specialties")
	void getSpecialties_withAddedSpecialties_returnsListWithThem() {
		Specialty s1 = new Specialty();
		s1.setName("Specialty1");
		Specialty s2 = new Specialty();
		s2.setName("Specialty2");
		instance.addSpecialty(s1);
		instance.addSpecialty(s2);

		List<Specialty> result = instance.getSpecialties();

		assertNotNull(result);
		assertEquals(2, result.size());
		assertTrue(result.contains(s1));
		assertTrue(result.contains(s2));
	}

	// =========================================================================
	// Tests for getNrOfSpecialties()
	// =========================================================================

	@Test
	@DisplayName("getNrOfSpecialties should return zero initially")
	void getNrOfSpecialties_initially_returnsZero() {
		int result = instance.getNrOfSpecialties();

		assertEquals(0, result);
	}

	@Test
	@DisplayName("getNrOfSpecialties should return count of added specialties")
	void getNrOfSpecialties_withAddedSpecialties_returnsCount() {
		instance.addSpecialty(new Specialty());
		instance.addSpecialty(new Specialty());

		int result = instance.getNrOfSpecialties();

		assertEquals(2, result);
	}

}

