package com.business.entities;

import org.junit.jupiter.api.BeforeEach;
import org.junit.jupiter.api.Test;
import org.junit.jupiter.api.DisplayName;
import static org.junit.jupiter.api.Assertions.*;
import com.business.entities.Product;

class ProductTest {

    private Product instance;

    @BeforeEach
    void setUp() {
        instance = new Product();
    }

    @Test
    @DisplayName("setPid should set the pid value and getPid should return it")
    void setPid_setsPidValue_andGetPidReturnsIt() {
        instance.setPid(123);
        assertEquals(123, instance.getPid());
    }

    @Test
    @DisplayName("getPid should return zero when not set")
    void getPid_returnsZeroInitially() {
        assertEquals(0, instance.getPid());
    }

    @Test
    @DisplayName("setPname should set the pname value and getPname should return it")
    void setPname_setsPnameValue_andGetPnameReturnsIt() {
        instance.setPname("Test Product");
        assertEquals("Test Product", instance.getPname());
    }

    @Test
    @DisplayName("getPname should return null when not set")
    void getPname_returnsNullInitially() {
        assertNull(instance.getPname());
    }

    @Test
    @DisplayName("setPprice should set the pprice value and getPprice should return it")
    void setPprice_setsPpriceValue_andGetPpriceReturnsIt() {
        instance.setPprice(99.99);
        assertEquals(99.99, instance.getPprice(), 0.001);
    }

    @Test
    @DisplayName("getPprice should return zero when not set")
    void getPprice_returnsZeroInitially() {
        assertEquals(0.0, instance.getPprice(), 0.001);
    }

    @Test
    @DisplayName("setPdescription should set the pdescription value and getPdescription should return it")
    void setPdescription_setsPdescriptionValue_andGetPdescriptionReturnsIt() {
        instance.setPdescription("A sample product description.");
        assertEquals("A sample product description.", instance.getPdescription());
    }

    @Test
    @DisplayName("getPdescription should return null when not set")
    void getPdescription_returnsNullInitially() {
        assertNull(instance.getPdescription());
    }

    @Test
    @DisplayName("toString should return a non-null string")
    void toString_returnsNonNullString() {
        String result = instance.toString();
        assertNotNull(result);
    }

    @Test
    @DisplayName("toString should include pid value after setting")
    void toString_includesPidValue() {
        instance.setPid(456);
        String result = instance.toString();
        assertTrue(result.contains("456"));
    }

}

