package com.business.entities;

import org.junit.jupiter.api.BeforeEach;
import org.junit.jupiter.api.Test;
import org.junit.jupiter.api.DisplayName;
import static org.junit.jupiter.api.Assertions.*;
import java.util.ArrayList;
import java.util.List;

class UserTest {

    private User instance;

    @BeforeEach
    void setUp() {
        instance = new User();
    }

    // =========================================================================
    // Tests for getOrders() and setOrders(List<Orders> orders)
    // =========================================================================

    @Test
    @DisplayName("setOrders should set the orders list and getOrders should return it")
    void setOrders_withList_setsAndGetsCorrectly() {
        List<Orders> orders = new ArrayList<>();
        Orders order1 = new Orders();
        Orders order2 = new Orders();
        orders.add(order1);
        orders.add(order2);

        instance.setOrders(orders);

        List<Orders> result = instance.getOrders();
        assertNotNull(result);
        assertEquals(2, result.size());
        assertTrue(result.contains(order1));
        assertTrue(result.contains(order2));
    }

    @Test
    @DisplayName("setOrders should handle empty list correctly")
    void setOrders_withEmptyList_setsAndGetsEmpty() {
        List<Orders> orders = new ArrayList<>();

        instance.setOrders(orders);

        List<Orders> result = instance.getOrders();
        assertNotNull(result);
        assertTrue(result.isEmpty());
    }

    // =========================================================================
    // Tests for getU_id() and setU_id(int u_id)
    // =========================================================================

    @Test
    @DisplayName("setU_id should set the u_id and getU_id should return it")
    void setU_id_withValue_setsAndGetsCorrectly() {
        int uId = 123;

        instance.setU_id(uId);

        int result = instance.getU_id();
        assertEquals(123, result);
    }

    @Test
    @DisplayName("setU_id should handle zero value correctly")
    void setU_id_withZero_setsAndGetsZero() {
        int uId = 0;

        instance.setU_id(uId);

        int result = instance.getU_id();
        assertEquals(0, result);
    }

    // =========================================================================
    // Tests for getUname() and setUname(String uname)
