    @Test
    @DisplayName("setUname should set the uname and getUname should return it")
    void setUname_withValue_setsAndGetsCorrectly() {
        String uname = "testuser";

        instance.setUname(uname);

        String result = instance.getUname();
        assertNotNull(result);
    }

    @Test
    @DisplayName("setUname should handle null value correctly")
    void setUname_withNull_setsAndGetsNull() {
        String uname = null;

        instance.setUname(uname);

        String result = instance.getUname();
        assertNull(result);
    }

    // =========================================================================
    // Tests for getUemail() and setUemail(String uemail)
    // =========================================================================

    @Test
    @DisplayName("setUemail should set the uemail and getUemail should return it")
    void setUemail_withValue_setsAndGetsCorrectly() {
        String uemail = "test@example.com";

        instance.setUemail(uemail);

        String result = instance.getUemail();
        assertNotNull(result);
    }

    @Test
    @DisplayName("setUemail should handle empty string correctly")
    void setUemail_withEmptyString_setsAndGetsEmpty() {
        String uemail = "";

        instance.setUemail(uemail);

        String result = instance.getUemail();
        assertEquals("", result);
    }

    // =========================================================================
    // Tests for getUpassword() and setUpassword(String upassword)
    // =========================================================================

    @Test
    @DisplayName("setUpassword should set the upassword and getUpassword should return it")
    void setUpassword_withValue_setsAndGetsCorrectly() {
        String upassword = "password123";

        instance.setUpassword(upassword);

        String result = instance.getUpassword();
        assertNotNull(result);
    }

    @Test
    @DisplayName("setUpassword should handle special characters correctly")
    void setUpassword_withSpecialChars_setsAndGetsSpecialChars() {
        String upassword = "p@ssw0rd!";

        instance.setUpassword(upassword);

        String result = instance.getUpassword();
        assertEquals("p@ssw0rd!", result);
    }

    // =========================================================================
    // Tests for getUnumber() and setUnumber(Long unumber)
    // =========================================================================

    @Test
    @DisplayName("setUnumber should set the unumber and getUnumber should return it")
