        instance.setUnumber(unumber);

        Long result = instance.getUnumber();
        assertEquals(9876543210L, result);
    }

    @Test
    @DisplayName("setUnumber should handle null value correctly")
    void setUnumber_withNull_setsAndGetsNull() {
