        instance.setUnumber(unumber);

        Long result = instance.getUnumber();
        assertNull(result);
    }

    // =========================================================================
    // Tests for toString()
    // =========================================================================

    @Test
    @DisplayName("toString should return a non-null string")
    void toString_returnsNonNullString() {
        instance.setUname("testuser");
        instance.setUemail("test@example.com");

        String result = instance.toString();

        assertNotNull(result);
        assertFalse(result.isEmpty());
    }

    @Test
    @DisplayName("toString should return a string even with null fields")
    void toString_withNullFields_returnsString() {
        String result = instance.toString();

        assertNotNull(result);
    }
}

